        </div>
        <div class="header-center">
            <div class="search-container">
                <input type="text" class="search-input" placeholder="Rechercher des podcasts, articles...">
                <i class="fas fa-search search-icon"></i>
            </div>
        </div>
        <div class="header-actions">
            @if (!auth()->check())
                <!-- Boutons d'authentification (affichés si non connecté) -->
                <div class="auth-buttons">
                    <a href="/login" class="btn btn-outline">Connexion</a>
                    <a href="/register" class="btn btn-primary">Inscription</a>
                </div>
            @else
                <!-- Profil utilisateur (affiché si connecté) -->
                <div class="user-profile">
                    @if (auth()->user()->avatar)
                        <img src="{{ Storage::url(auth()->user()->avatar) }}" alt="Avatar Utilisateur" class="user-avatar">
                    @elseif (auth()->user()->provider)
                        <img src="https://via.placeholder.com/150" alt="Avatar Utilisateur" class="user-avatar">
                    @else
                        <div class="initial-avatar">{{ substr(auth()->user()->name, 0, 1) }}</div>
                    @endif
                    <span class="user-name">{{ auth()->user()->name }}</span>
                </div>
            @endif
        </div>
    </header>

    <script>
        document.addEventListener('DOMContentLoaded', function() {
            const userProfile = document.querySelector('.user-profile');
            const searchInput = document.querySelector('.search-input');

            if (userProfile) {
                userProfile.addEventListener('click', function(e) {
                    window.location.href = '/profile';
                });
            }

            if (searchInput) {
                searchInput.addEventListener('input', function() {
                    const searchTerm = this.value.trim().toLowerCase();
                    console.log('Recherche :', searchTerm);

                    const videosContainer = document.getElementById('videos-container');
                    if (videosContainer && window.videosData) {
                        console.log('Nombre total de vidéos dans videosData:', window.videosData.length);
                        const filteredVideos = window.videosData.filter(video => {
                            const title = (video.snippet.title || '').toLowerCase();
                            const description = (video.snippet.description || '').toLowerCase();
                            const publishedAt = video.snippet.publishedAt 
                                ? new Date(video.snippet.publishedAt).toLocaleDateString('fr-FR').toLowerCase() 
                                : '';
                            console.log('Vérification pour vidéo:', { title, description, publishedAt });
                            return title.includes(searchTerm) || description.includes(searchTerm) || publishedAt.includes(searchTerm);
                        });
                        console.log('Nombre de vidéos filtrées:', filteredVideos.length);

                        window.currentPage = 1;

                        if (typeof window.displayFilteredVideos === 'function') {
                            window.displayFilteredVideos(filteredVideos);
                            if (typeof window.displayPagination === 'function') {
                                window.displayPagination(filteredVideos);
                            }
                        } else {
                            console.error('displayFilteredVideos n\'est pas défini');
                        }
                    } else {
                        console.error('videosContainer ou window.videosData non trouvés');
                    }
                });
            }
        });
    </script>
</body>
</html>